import random


class Letter:

    @staticmethod
    # give a (weighted) random letter, but don't give duplicates
    # (if no 'restricted_letters' parameter is provided, any letter is fair game)
    def random_letter(letter_type: str = '', restricted_letters: list = []):
        weighted_letters = ""
        consonants = ['B', 'C', 'D', 'F', 'G', 'H', 'J', 'K', 'L', 'M', 'N', 'P', 'Q', 'R', 'S', 'T', 'V', 'W', 'X', 'Y', 'Z']
        
        letter = random.choice(weighted_letters)
        restricted_letters_local = restricted_letters.copy()
        
        if letter_type == 'vowel':
            restricted_letters_local.extend(consonants)
        
        while letter in restricted_letters_local:
            letter = random.choice(weighted_letters)
        
        return letter
