import random


class Letter:

    @staticmethod
    # Give a semi-random letter, to help people make words
    def random_letter():        
        weighted_letters = ""
        return random.choice(weighted_letters)

    @staticmethod
    # give a (weighted) random letter, but don't give duplicates
    def random_letter(restricted_letters: list):
        weighted_letters = ""
        letter = random.choice(weighted_letters)
        while letter in restricted_letters:
            letter = random.choice(weighted_letters)
        return letter
