import random


class Letter:

    @staticmethod
    # give a (weighted) random letter, but don't give duplicates
    # (if no 'restricted_letters' parameter is provided, any letter is fair game)
    def random_letter(restricted_letters: list = []):
        weighted_letters = ""
        letter = random.choice(weighted_letters)
        while letter in restricted_letters:
            letter = random.choice(weighted_letters)
        return letter
